mapper.select:
select
  id, fk_province, fk_timezone, name, pinyin, enname, remark, status, valid, fk_user_create, createtime, fk_user_modify, modifytime, code
from common_db.city


        and id = #{id}


        and fk_province = #{fkProvince}


        and fk_timezone = #{fkTimezone}


        and name = #{name}


        and pinyin = #{pinyin}


        and enname = #{enname}


        and remark = #{remark}


        and status = #{status}


        and valid = #{valid}


        and fk_user_create = #{fkUserCreate}


        and createtime = #{createtime}


        and fk_user_modify = #{fkUserModify}


        and modifytime = #{modifytime}


        and code = #{code}
